package org.acme;import jakarta.ws.rs.*;import jakarta.ws.rs.core.MediaType;import jakarta.ws.rs.core.Response;import jakarta.ws.rs.core.UriBuilder;@Path("de")public class DeveloperResource {    @GET    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response all(Dev dev) {        dev.persist();        return Response.ok().build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response create(Dev dev) {        dev.persist();        return Response.created(                        UriBuilder                                .fromResource(DeveloperResource.class)                                .path(Long.toString(dev.getId()))                                .build()                )                .entity(dev)                .build();    }    public static class Dev {        static long counter = 1;        private long id;        private String name;        public long getId() {            return this.id = id;        }        public void setId(long id) {            this.id = id;        }        public void setId(String name) {            this.name = name;        }        public String getName() {            return this.name = name;        }        public void persist() {            this.id = counter++;        }    }}